"use strict";const btn=document.querySelector(".btn-country"),countriesContainer=document.querySelector(".countries"),renderCountry=function(data){const html=`\n  <article class="country">\n  <img class="country__img" src="${data.flags.png}" />\n  <div class="country__data">\n      <h3 class="country__name">${data.name.common}</h3>\n      <h4 class="country__region">${data.region}</h4>\n      <p class="country__row"><span>👫</span>${(+data.population/1e6).toFixed(1)} people</p>\n      <p class="country__row"><span>🗣️</span>${Object.values(data.languages)[0]}</p>\n      <p class="country__row"><span>💰</span>${Object.values(data.currencies)[0].name}</p>\n  </div>\n  </article>\n  `;countriesContainer.insertAdjacentHTML("beforeend",html),countriesContainer.style.opacity=1},getCountryAndNeigbor=function(country){const request=new XMLHttpRequest;request.open("GET",`https://restcountries.com/v3.1/name/${country}`),request.send(),request.addEventListener("load",(function(){const[data]=JSON.parse(this.responseText);console.log(data),renderCountry(data);const[neighbour]=data.borders;if(!neighbour)return;const request2=new XMLHttpRequest;request2.open("GET",`https://restcountries.com/v3.1/alpha/${neighbour}`),request2.send(),request2.addEventListener("load",(function(){const[data]=JSON.parse(this.responseText);console.log(data),renderCountry(data);const[neighbour2]=data.borders;if(!neighbour2)return;const request3=new XMLHttpRequest;request3.open("GET",`https://restcountries.com/v3.1/alpha/${neighbour2}`),request3.send(),request3.addEventListener("load",(function(){const[data]=JSON.parse(this.responseText);console.log(data),renderCountry(data)}))}))}))};getCountryAndNeigbor("portugal");