"use strict";const btn=document.querySelector(".btn-country"),countriesContainer=document.querySelector(".countries"),renderCountry=function(data,classname=""){const html=`\n  <article class="country ${classname}">\n  <img class="country__img" src="${data.flags.png}" />\n  <div class="country__data">\n      <h3 class="country__name">${data.name.common}</h3>\n      <h4 class="country__region">${data.region}</h4>\n      <p class="country__row"><span>👫</span>${(+data.population/1e6).toFixed(1)} people</p>\n      <p class="country__row"><span>🗣️</span>${Object.values(data.languages)[0]}</p>\n      <p class="country__row"><span>💰</span>${Object.values(data.currencies)[0].name}</p>\n  </div>\n  </article>\n  `;countriesContainer.insertAdjacentHTML("beforeend",html)},renderError=function(message){countriesContainer.insertAdjacentText("beforeend",message)},getJSON=function(url,errorMsg="Something went wrong!"){return fetch(url).then(response=>{if(!response.ok)throw new Error(`${errorMsg} (${response.status})`);return response.json()})},getCountryData=function(country){getJSON(`https://restcountries.com/v3.1/name/${country}`,"Country not found!").then(data=>{renderCountry(data[0]);const neighbour="wwasdasd";return getJSON("https://restcountries.com/v3.1/alpha/wwasdasd","Country not found!")}).then(data=>renderCountry(data[0],"neighbour")).catch(err=>{console.log(`${err} 💥💥💥`),renderError(`Something went wrong 💥💥 ${err.message}. Try Again!`)}).finally(countriesContainer.style.opacity=1)};btn.addEventListener("click",(function(){getCountryData("portugal")}));