"use strict";const btn=document.querySelector(".btn-country"),countriesContainer=document.querySelector(".countries"),renderCountry=function(data,classname=""){const html=`\n  <article class="country ${classname}">\n  <img class="country__img" src="${data.flags.png}" />\n  <div class="country__data">\n      <h3 class="country__name">${data.name.common}</h3>\n      <h4 class="country__region">${data.region}</h4>\n      <p class="country__row"><span>👫</span>${(+data.population/1e6).toFixed(1)} people</p>\n      <p class="country__row"><span>🗣️</span>${Object.values(data.languages)[0]}</p>\n      <p class="country__row"><span>💰</span>${Object.values(data.currencies)[0].name}</p>\n  </div>\n  </article>\n  `;countriesContainer.insertAdjacentHTML("beforeend",html),countriesContainer.style.opacity=1},renderError=function(message){countriesContainer.insertAdjacentText("beforeend",message)},getJSON=function(url,errorMsg="Something went wrong!"){return fetch(url).then(response=>{if(!response.ok)throw new Error(`${errorMsg} (${response.status})`);return response.json()})},get3Countries=async function(c1,c2,c3){try{const data=await Promise.all([getJSON(`https://restcountries.com/v3.1/name/${c1}`),getJSON(`https://restcountries.com/v3.1/name/${c2}`),getJSON(`https://restcountries.com/v3.1/name/${c3}`)]);console.log(data.map(d=>d[0].capital[0]))}catch(err){console.error(err.message)}};get3Countries("portugal","canada","tanzania");