"use strict";const btn=document.querySelector(".btn-country"),countriesContainer=document.querySelector(".countries"),getCountryData=function(country){const request=new XMLHttpRequest;request.open("GET",`https://restcountries.com/v3.1/name/${country}`),request.send(),request.addEventListener("load",(function(){const[data]=JSON.parse(this.responseText);console.log(data);const html=`\n    <article class="country">\n    <img class="country__img" src="${data.flags.png}" />\n    <div class="country__data">\n        <h3 class="country__name">${data.name.common}</h3>\n        <h4 class="country__region">${data.region}</h4>\n        <p class="country__row"><span>👫</span>${(+data.population/1e6).toFixed(1)} people</p>\n        <p class="country__row"><span>🗣️</span>${Object.values(data.languages)[0]}</p>\n        <p class="country__row"><span>💰</span>${Object.values(data.currencies)[0].name}</p>\n    </div>\n    </article>\n    `;countriesContainer.insertAdjacentHTML("beforeend",html),countriesContainer.style.opacity=1}))};getCountryData("philippines"),getCountryData("usa"),getCountryData("canada"),getCountryData("germany"),getCountryData("israel");